import React, { useState, useRef, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import './AIChat.css';

const AIChat = () => {
  const { isAuthenticated } = useAuth();
  const [isOpen, setIsOpen] = useState(false);
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef(null);

  const toggleChat = () => setIsOpen(!isOpen);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = async (endpoint) => {
    if (!input.trim()) return;

    const userMessage = { text: input, sender: 'user' };
    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setIsLoading(true);

    try {
      // Здесь будет реальный запрос к вашему API
      // Это заглушка для демонстрации
      await new Promise(resolve => setTimeout(resolve, 1000));
      const aiMessage = { 
        text: `Это ответ на ваш запрос (${endpoint === 0 ? 'обычный' : 'chain-of-thought'})`, 
        sender: 'ai' 
      };
      setMessages(prev => [...prev, aiMessage]);
    } catch (error) {
      setMessages(prev => [...prev, { 
        text: `Error: ${error.message}`, 
        sender: 'ai',
        isError: true 
      }]);
    } finally {
      setIsLoading(false);
    }
  };

  if (!isAuthenticated) {
    return (
      <div className="ai-chat-container">
        <div className="auth-message">
          Пользоваться ИИ-ассистентом возможно только после авторизации на сайте
          <br />
          <a href="/auth">Войти или Зарегистрироваться</a>
        </div>
      </div>
    );
  }

  return (
    <div className="ai-chat-container">
      <button className="ai-chat-toggle" onClick={toggleChat}>
        AI
      </button>
      
      <div className={`ai-chat-window ${isOpen ? 'active' : ''}`}>
        <div className="ai-chat-header">
          <div className="ai-chat-title">ИИ-ассистент</div>
          <button className="ai-chat-close" onClick={toggleChat}>
            ×
          </button>
        </div>
        
        <div className="ai-chat-messages">
          {messages.map((message, index) => (
            <div 
              key={index} 
              className={`message ${message.sender}-message ${message.isError ? 'error' : ''}`}
            >
              {message.text}
            </div>
          ))}
          {isLoading && (
            <div className="message ai-message">
              ИИ думает...
            </div>
          )}
          <div ref={messagesEndRef} />
        </div>
        
        <div className="ai-chat-input-area">
          <textarea
            className="ai-chat-textarea"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Введите ваш запрос..."
            onKeyPress={(e) => {
              if (e.key === 'Enter' && !e.shiftKey) {
                e.preventDefault();
                handleSendMessage(0);
              }
            }}
          />
          <div className="ai-chat-buttons">
            <button 
              className="ai-chat-button secondary"
              onClick={() => handleSendMessage(1)}
              disabled={isLoading || !input.trim()}
            >
              Chain-of-thought
            </button>
            <button 
              className="ai-chat-button primary"
              onClick={() => handleSendMessage(0)}
              disabled={isLoading || !input.trim()}
            >
              Отправить
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AIChat;
