import React, { useState, useRef, useEffect } from 'react';
import '../styles/ai.css';

const AIChat = ({ isAuthenticated }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [history, setHistory] = useState([]);
  const [showHistory, setShowHistory] = useState(false);
  const messagesEndRef = useRef(null);

  const toggleChat = () => setIsOpen(!isOpen);
  const toggleHistory = () => setShowHistory(!showHistory);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    if (isOpen && isAuthenticated) {
      fetchHistory();
    }
  }, [isOpen, isAuthenticated]);

  const fetchHistory = async () => {
    try {
      const response = await fetch('/history', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });
      const data = await response.json();
      if (response.ok) {
        setHistory(data.history || []);
      }
    } catch (error) {
      console.error('Failed to fetch history:', error);
    }
  };

  const loadHistoryItem = (item) => {
    setMessages(item.messages);
    setShowHistory(false);
    scrollToBottom();
  };

  const handleSendMessage = async (endpoint) => {
    if (!input.trim()) return;

    const userMessage = { text: input, sender: 'user' };
    const newMessages = [...messages, userMessage];
    setMessages(newMessages);
    setInput('');
    setIsLoading(true);

    try {
      const response = await fetch(`/ai/${endpoint}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({ prompt: input })
      });

      const data = await response.json();
      if (response.ok) {
        const aiMessage = { text: data.response, sender: 'ai' };
        setMessages([...newMessages, aiMessage]);
        await fetchHistory();
      } else {
        throw new Error(data.error || 'Failed to get response');
      }
    } catch (error) {
      setMessages([...newMessages, { 
        text: `Error: ${error.message}`, 
        sender: 'ai',
        isError: true 
      }]);
    } finally {
      setIsLoading(false);
    }
  };

  if (!isAuthenticated) {
    return (
      <div className="ai-chat-container">
        <div className="auth-message">
          –ü–æ–ª—å–∑–æ–≤–∞—Ç—å—Å—è –ò–ò-–∞—Å—Å–∏—Å—Ç–µ–Ω—Ç–æ–º –≤–æ–∑–º–æ–∂–Ω–æ —Ç–æ–ª—å–∫–æ –ø–æ—Å–ª–µ –∞–≤—Ç–æ—Ä–∏–∑–∞—Ü–∏–∏ –Ω–∞ —Å–∞–π—Ç–µ
          <br />
          <a href="/auth">–í–æ–π—Ç–∏ –∏–ª–∏ –ó–∞—Ä–µ–≥–∏—Å—Ç—Ä–∏—Ä–æ–≤–∞—Ç—å—Å—è</a>
        </div>
      </div>
    );
  }

  return (
    <div className="ai-chat-container">
      <button className="ai-chat-toggle" onClick={toggleChat}>
        AI
      </button>
      
      <div className={`ai-chat-window ${isOpen ? 'active' : ''}`}>
        <div className="ai-chat-header">
          <div className="ai-chat-title">–ò–ò-–∞—Å—Å–∏—Å—Ç–µ–Ω—Ç</div>
          <div className="ai-chat-tools">
            <button className="tool-button" onClick={toggleHistory} title="–ò—Å—Ç–æ—Ä–∏—è –∑–∞–ø—Ä–æ—Å–æ–≤">
              üïí
            </button>
            <button className="ai-chat-close" onClick={toggleChat}>
              √ó
            </button>
          </div>
        </div>
        
        <div className="ai-chat-messages">
          {messages.length === 0 && !isLoading && (
            <div className="message ai-message">
              –ó–∞–¥–∞–π—Ç–µ –≤–æ–ø—Ä–æ—Å –ò–ò-–∞—Å—Å–∏—Å—Ç–µ–Ω—Ç—É
            </div>
          )}
          {messages.map((message, index) => (
            <div 
              key={index} 
              className={`message ${message.sender}-message ${message.isError ? 'error' : ''}`}
            >
              {message.text}
            </div>
          ))}
          {isLoading && (
            <div className="message ai-message">
              –ò–ò –¥—É–º–∞–µ—Ç...
            </div>
          )}
          <div ref={messagesEndRef} />
        </div>
        
        <div className="ai-chat-input-area">
          <textarea
            className="ai-chat-textarea"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="–í–≤–µ–¥–∏—Ç–µ –≤–∞—à –∑–∞–ø—Ä–æ—Å..."
            onKeyPress={(e) => {
              if (e.key === 'Enter' && !e.shiftKey) {
                e.preventDefault();
                handleSendMessage(0);
              }
            }}
          />
          <div className="ai-chat-buttons">
            <button 
              className="ai-chat-button secondary"
              onClick={() => handleSendMessage(1)}
              disabled={isLoading || !input.trim()}
            >
              Chain-of-thought
            </button>
            <button 
              className="ai-chat-button primary"
              onClick={() => handleSendMessage(0)}
              disabled={isLoading || !input.trim()}
            >
              –û—Ç–ø—Ä–∞–≤–∏—Ç—å
            </button>
          </div>
        </div>
      </div>

      <div className={`ai-chat-history ${showHistory ? 'active' : ''}`}>
        <div className="history-header">
          <span>–ò—Å—Ç–æ—Ä–∏—è</span>
        </div>
        {history.length === 0 ? (
          <div className="history-item">–ò—Å—Ç–æ—Ä–∏—è –ø—É—Å—Ç–∞</div>
        ) : (
          history.map((item, index) => (
            <div 
              key={index} 
              className="history-item"
              onClick={() => loadHistoryItem(item)}
            >
              {item.messages[0]?.text.slice(0, 50)}...
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default AIChat;
