import React, { useState, useEffect, useContext } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import AuthContext from '../context/AuthContext';
import '../styles/edit-build.css';

const componentTypes = {
  cpu: 'Процессор',
  gpu: 'Видеокарта',
  motherboard: 'Материнская плата',
  ram: 'Оперативная память',
  storage: 'Накопитель',
  psu: 'Блок питания',
  cpucool: 'Кулер',
  case: 'Корпус'
};

const EditBuildPage = () => {
  const [searchResults, setSearchResults] = useState([]);
  const [buildName, setBuildName] = useState('');
  const [selectedComponents, setSelectedComponents] = useState({});
  const [searchQuery, setSearchQuery] = useState('');
  const [allComponents, setAllComponents] = useState([]); // Все компоненты с сервера
  const [activeComponent, setActiveComponent] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();
  const location = useLocation();

  // Сброс поиска при изменении активного компонента
  useEffect(() => {
    setSearchQuery('');
  }, [activeComponent]);

  // Загрузка существующей сборки
  useEffect(() => {
    if (location.state?.build) {
      setBuildName(location.state.build.name);
      setSelectedComponents(location.state.build.components || {});
    }
  }, [location]);

  // Получение всех компонентов
  const fetchAllComponents = async () => {
    try {
      const response = await axios.get(`http://localhost:8000/all_components`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      });
      setAllComponents(response.data);
    } catch (error) {
      console.error('Ошибка загрузки компонентов:', error);
      alert('Не удалось загрузить список компонентов');
    }
  };

  // Подписываемся на получение всех компонентов при монтировании
  useEffect(() => {
    fetchAllComponents();
  }, []);

  // Поиск и фильтрация на фронтенде
  const handleSearch = () => {
    if (!activeComponent) return;

    const query = searchQuery.toLowerCase();

    // Фильтруем локально:
    // 1. По типу (cpu, gpu...)
    // 2. По названию
    const filtered = allComponents.filter(comp => {
      const matchesType = comp.type === activeComponent;
      const matchesName = comp.name.toLowerCase().includes(query);
      return matchesType && matchesName;
    });

    setSearchResults(filtered);
  };

  // Выбор компонента
  const selectComponent = (component) => {
    setSelectedComponents(prev => ({
      ...prev,
      [activeComponent]: {
        id: component.id,
        name: component.name,
        price: parseFloat(component.price),
        type: component.type
      }
    }));
    setActiveComponent(null);
  };

  // Расчет общей стоимости
  const calculateTotal = () => {
    return Object.values(selectedComponents)
      .reduce((sum, comp) => sum + (comp?.price || 0), 0)
      .toFixed(2);
  };

  // Сохранение сборки
  const saveBuild = async () => {
    if (!buildName.trim()) {
      alert('Введите название сборки');
      return;
    }

    if (Object.keys(selectedComponents).length === 0) {
      alert('Добавьте хотя бы один компонент');
      return;
    }

    try {
      const endpoint = location.state?.build 
        ? `/builds/${location.state.build.id}`
        : '/builds';

      await axios({
        method: location.state?.build ? 'PUT' : 'POST',
        url: `http://localhost:8000${endpoint}`,
        data: {
          name: buildName,
          components: selectedComponents
        },
        headers: { 
          Authorization: `Bearer ${localStorage.getItem('token')}` 
        }
      });

      navigate('/builds');
    } catch (error) {
      console.error('Ошибка сохранения:', error.response?.data?.detail || error.message);
      alert('Ошибка сохранения сборки');
    }
  };

  if (!user) {
    navigate('/auth');
    return null;
  }

  return (
    <div className="edit-build-container">
      <div className="build-header">
        <input
          type="text"
          placeholder="Название сборки"
          value={buildName}
          onChange={(e) => setBuildName(e.target.value)}
          className="build-name-input"
        />
        <div className="total-price">
          Итоговая стоимость: {calculateTotal()} ₽
        </div>
      </div>

      <div className="components-grid">
        {Object.entries(componentTypes).map(([key, label]) => (
          <div 
            key={key}
            className={`component-card ${activeComponent === key ? 'active' : ''}`}
          >
            <div 
              className="component-header" 
              onClick={() => setActiveComponent(prev => prev === key ? null : key)}
            >
              <h4>{label}</h4>
              <div className="selected-component">
                {selectedComponents[key]?.name || 'Не выбрано'}
                {selectedComponents[key]?.price && (
                  <span className="component-price">
                    {selectedComponents[key].price.toFixed(2)} ₽
                  </span>
                )}
              </div>
            </div>

            {activeComponent === key && (
              <div className="search-section">
                <form className="search-bar" onSubmit={(e) => { e.preventDefault(); handleSearch(); }}>
                  <input
                    type="text"
                    placeholder={`Поиск ${label.toLowerCase()}...`}
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                  />
                  <button type="submit" disabled={isLoading}>
                    {isLoading ? 'Поиск...' : 'Найти'}
                  </button>
                </form>

                {searchResults.length > 0 && (
                  <div className="search-results">
                    {searchResults.map((item) => (
                      <div 
                        key={item.id} 
                        className="result-item"
                        onClick={() => selectComponent(item)}
                      >
                        <div className="part-info">
                          <div className="part-name">{item.name}</div>
                        </div>
                        <div className="part-price">
                          {parseFloat(item.price).toFixed(2)} ₽
                        </div>
                      </div>
                    ))}
                  </div>
                )}

                {searchResults.length === 0 && searchQuery && !isLoading && (
                  <div className="no-results">Ничего не найдено</div>
                )}
              </div>
            )}
          </div>
        ))}
      </div>

      <div className="actions">
        <button onClick={saveBuild} className="save-btn">
          {location.state?.build ? 'Обновить сборку' : 'Сохранить сборку'}
        </button>
        <button onClick={() => navigate('/builds')} className="cancel-btn">
          Отмена
        </button>
      </div>
    </div>
  );
};

export default EditBuildPage;
