import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { v4 as uuidv4 } from 'uuid'; // Для генерации временного user_id

const AIpage = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [userId] = useState(() => localStorage.getItem('userId') || uuidv4()); // Генерация или получение user_id

  // Сохраняем user_id в localStorage при первом рендере
  useEffect(() => {
    localStorage.setItem('userId', userId);
  }, [userId]);

  // Загрузка истории при монтировании компонента
  useEffect(() => {
    const loadHistory = async () => {
      try {
        const response = await axios.get('http://localhost:8000/history', {
          params: { user_id: userId },
        });
        setMessages(response.data);
      } catch (error) {
        console.error('Ошибка загрузки истории:', error);
      }
    };
    loadHistory();
  }, [userId]);

  // Отправка запроса к ИИ
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim() || isLoading) return;

    setIsLoading(true);
    const newMessage = { request_text: input, is_ai: false };

    try {
      // Оптимистичное обновление интерфейса
      setMessages((prev) => [...prev, newMessage]);
      setInput('');

      // Запрос к бэкенду
      const response = await axios.post('http://localhost:8000/ai', {
        user_id: userId,
        message: input,
      });

      // Добавляем ответ ИИ в историю
      setMessages((prev) => [
        ...prev,
        {
          request_text: input,
          response_text: response.data.response,
          created_at: new Date().toISOString(),
          is_ai: true,
        },
      ]);
    } catch (error) {
      console.error('Ошибка запроса:', error);
      // Откатываем оптимистичное обновление
      setMessages((prev) => prev.filter((msg) => msg !== newMessage));
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="ai-chat">
      <div className="chat-messages">
        {messages.map((msg, index) => (
          <div key={index} className={`message ${msg.is_ai ? 'ai' : 'user'}`}>
            <div className="message-content">
              {msg.is_ai ? (
                <>
                  <div className="ai-response">{msg.response_text}</div>
                  <div className="timestamp">
                    {new Date(msg.created_at).toLocaleTimeString()}
                  </div>
                </>
              ) : (
                <div className="user-request">{msg.request_text}</div>
              )}
            </div>
          </div>
        ))}
      </div>

      <form onSubmit={handleSubmit} className="chat-input">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Напишите запрос..."
          disabled={isLoading}
        />
        <button type="submit" disabled={isLoading}>
          {isLoading ? 'Отправка...' : 'Отправить'}
        </button>
      </form>
    </div>
  );
};

export default AIpage;
